package com.geeks.nurkyz_chomoeva_hw3_4;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.geeks.nurkyz_chomoeva_hw3_4.databinding.FragmentRVBinding;

import java.util.ArrayList;


public class RVFragment extends Fragment implements OnItemClick{

    FragmentRVBinding binding;
    private Integer position;
    private ArrayList<PizzaModel> pizzaModel = new ArrayList<>();


    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        binding = FragmentRVBinding.inflate(getLayoutInflater());
        return binding.getRoot();
    }


    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        String item = getArguments() != null ? getArguments().getString("item") : "";
        PizzaAdapter adapter = new PizzaAdapter(pizzaModel, this);
        binding.rvView.setAdapter(adapter);
        loadData(item);
    }

    private void loadData(String item) {
        pizzaModel.clear();
        if (item.equals("Пиццы")) {
            pizzaModel.add(new PizzaModel("Пеперони", "data:image/jpeg;base64,","Вкусная пицца","4$"));
            pizzaModel.add(new PizzaModel("Маргарита", "data:image/jpeg;base64,","Типичная пицца","3$"));
            pizzaModel.add(new PizzaModel("Четыре сыра", "data:image/jpeg;base64,","Много сыра","5$"));
            pizzaModel.add(new PizzaModel("Вегетаринаская", "data:image/jpeg;base64,","Легкая пицца","4$"));
            pizzaModel.add(new PizzaModel("Чили", "data:image/jpeg;base64,","Острая пицца","6$"));
        } else if (item.equals("Бургеры")) {
            pizzaModel.add(new PizzaModel("Классический", "data:image/jpeg;base64,","Вкусный бургер","4$"));
            pizzaModel.add(new PizzaModel("Чизбургер", "data:image/jpeg;base64,","С сыром","3$"));
            pizzaModel.add(new PizzaModel("Куринный", "data:image/jpeg;base64,","Из куриного фарша","5$"));
            pizzaModel.add(new PizzaModel("Вегетаринаская", "data:image/jpeg;base64,","Легкий бургер","4$"));
            pizzaModel.add(new PizzaModel("Чили", "data:image/jpeg;base64,","Острый бургер","6$"));
        } else if (item.equals("Комбо")) {
            pizzaModel.add(new PizzaModel("2 пиццы + напиток", "data:image/jpeg;base64,","Всё на ваш выбор","10$"));
            pizzaModel.add(new PizzaModel("3 пиццы", "data:image/jpeg;base64,","На выбор со скидкой","12$"));
            pizzaModel.add(new PizzaModel("4 пиццы", "data:image/jpeg;base64,","На выбор со скидкой","14$"));
            pizzaModel.add(new PizzaModel("2 пиццы + 1", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT7dG7YnFsWqCowMlYqAC4r3vx9ytbhYYnfrw&usqp=CAU","Всё на ваш выбор","10$"));
            pizzaModel.add(new PizzaModel("Пицца + бургер + напиток", "data:image/jpeg;base64,","Напиток в подарок","11$"));
        } else if (item.equals("Напитки")) {
            pizzaModel.add(new PizzaModel("Лимонад", "data:image/jpeg;base64,","Освежает в такую жару","4$"));
            pizzaModel.add(new PizzaModel("Кола", "data:image/jpeg;base64,","Всегда в тренде","3$"));
            pizzaModel.add(new PizzaModel("Фанта", "data:image/jpeg;base64,","Любителям апельсина","3$"));
            pizzaModel.add(new PizzaModel("Чай", "data:image/jpeg;base64,","Согревает в любой холод","1$"));
            pizzaModel.add(new PizzaModel("Кофе", "data:image/jpeg;base64,","Будешь бодрым весь день","2$"));
        } else if (item.equals("Десерты")) {
            pizzaModel.add(new PizzaModel("Тирамису", "data:image/jpeg;base64,","Сочный десерт","4$"));
            pizzaModel.add(new PizzaModel("Наполеон", "data:image/jpeg;base64,","Сладкий десерт","3$"));
            pizzaModel.add(new PizzaModel("Макаронсы", "data:image/jpeg;base64,","Вкусный десерт","5$"));
            pizzaModel.add(new PizzaModel("Мороженое", "data:image/jpeg;base64,","Тает во рту","4$"));
            pizzaModel.add(new PizzaModel("Шоколад Кыргызстан ❤", "data:image/jpeg;base64,","Топ из шоколада","6$"));
        }
    }


    @Override
    public void onClick(int position) {
        PizzaModel pizza = pizzaModel.get(position);
        Bundle bundle = new Bundle();
        bundle.putSerializable("pizza", pizza);
        DetailFragment fragment = new DetailFragment();
        fragment.setArguments(bundle);
        requireActivity().getSupportFragmentManager().
                beginTransaction().replace(R.id.fragment_container_view, fragment).
                addToBackStack(null).commit();
    }

}