package com.geeks.nurkyz_chomoeva_hw3_4.category;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

import com.geeks.nurkyz_chomoeva_hw3_4.OnItemClick;
import com.geeks.nurkyz_chomoeva_hw3_4.PizzaModel;
import com.geeks.nurkyz_chomoeva_hw3_4.R;
import com.geeks.nurkyz_chomoeva_hw3_4.RVFragment;
import com.geeks.nurkyz_chomoeva_hw3_4.databinding.FragmentRV1Binding;

import java.util.ArrayList;
import java.util.List;

public class CategoryFragment extends Fragment implements OnItemClick {

    FragmentRV1Binding binding;
    private ArrayList<Category> categoryList = new ArrayList<>();


    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {
        binding = FragmentRV1Binding.inflate(getLayoutInflater());
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        loadCategories();
        CategoryAdapter adapter = new CategoryAdapter(categoryList, this);
        binding.rvView2.setAdapter(adapter);
    }

    private void loadCategories() {
        categoryList.add(new Category("Пиццы","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSh4d_yhMG4iHXZpoMmQkj_lqsB1yTQP8C7UA&usqp=CAU"));
        categoryList.add(new Category("Бургеры","data:image/jpeg;base64,"));
        categoryList.add(new Category("Комбо","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSFhRKAUmxVaXCwznrCzGRUX2D9-2uES5M22g&usqp=CAU"));
        categoryList.add(new Category("Напитки","data:image/jpeg;base64,"));
        categoryList.add(new Category("Десерты","data:image/jpeg;base64,"));
    }

    @Override
    public void onClick(int position) {
        Bundle bundle = new Bundle();
        bundle.putString("keyForPosition", String.valueOf(position));
        if (position == 0) {
            bundle.putString("item", "Пиццы");
        } else if (position == 1) {
            bundle.putString("item", "Бургеры");
        } else if (position == 2) {
            bundle.putString("item", "Комбо");
        } else if (position == 3) {
            bundle.putString("item", "Напитки");
        } else if (position == 4) {
            bundle.putString("item", "Десерты");
        }
        RVFragment RVFragment = new RVFragment();
        RVFragment.setArguments(bundle);
        requireActivity().getSupportFragmentManager().
                beginTransaction().replace(R.id.fragment_container_view, RVFragment).
                addToBackStack(null).commit();
    }


}